	private static String TEST_EMAIL = "s@bht.de";
	private static String TEST_PWD = "000000";

	/**
	 * a kind of highly simplistic authentication implementation
	 */
	@Override
	public boolean authenticateUser(User user) {
		logger.info("authenticateUser(): " + user);
		return TEST_EMAIL.equals(user.getEmail()) && TEST_PWD.equals(user.getPwd());
	}

	@Override
	public boolean prepare(String email, String pwd) {
		if (email != null && VALID_EMAIL_ADDRESS_REGEX.matcher(email).matches()) {
			if (pwd != null && pwd.length() == 6) {
				TEST_EMAIL = email;
				TEST_PWD = pwd;
				return true;
			}
			else {
				throw new InternalServerErrorException("Password address provided for authentication preparation is not allowed: " + pwd);
			}
		}
		else {
			throw new InternalServerErrorException("Email address provided for authentication preparation is not allowed: " + email);
		}
	}

